<?php/** * Prestablog tab for admin panel * @category admin * * @author Appside  * @copyright Appside * @link appside.net *  */require_once(_PS_MODULE_DIR_ . 'myfriends/classes/FriendsInfo.php');class AdminFriendsInfoController extends ModuleAdminController {    public function __construct() {        $this->table = 'friend';        $this->className = 'FriendsInfo';        $this->module = 'myfriends';             $this->addRowAction('edit');        $this->addRowAction('delete');        $this->bulk_actions = array('delete' => array('text' => $this->l('Delete selected'), 'confirm' => $this->l('Delete selected items?')));        $this->fields_list = array(            'id_friend' => array('title' => $this->l('ID'), 'align' => 'center', 'width' => 30),            'name' => array('title' => $this->l('Name'), 'width' => 'auto'),            'website' => array('title' => $this->l('Website'), 'width' => auto, 'maxlength' => 90, 'orderby' => false),            'facebook' => array('title' => $this->l('Facebook'), 'width' => 'auto'),            'position' => array('title' => $this->l('Position'), 'width' => 40, 'filter_key' => 'position', 'align' => 'center'),            'active' => array(                'title' => $this->l('Displayed'), 'width' => 25, 'active' => 'status',                'align' => 'center', 'type' => 'bool', 'orderby' => false        ));        parent::__construct();    }    public function renderForm() {        $this->toolbar_btn['save-and-stay'] = array('href' => '#', 'desc' => $this->l('Save and stay'));        $this->initToolbar();        if (!$this->loadObject(true))            return;        $this->fields_form = array(            'tinymce' => true,            'legend' => array(                'title' => $this->l('Friend'),                'image' => '../img/admin/tab-categories.gif'            ),            'input' => array(                array(                    'type' => 'text',                    'label' => $this->l('Name:'),                    'name' => 'name',                    'required' => true,                    'class' => 'copy2friendlyUrl',                    'hint' => $this->l('Invalid characters:') . ' <>;=#{}'                ),                array(                    'type' => 'text',                    'label' => $this->l('Website :'),                    'name' => 'website',                                       'class' => 'copy2friendlyUrl',                    'desc' => $this->l("This is friend's website")                )                ,                array(                    'type' => 'text',                    'label' => $this->l('Facebook :'),                    'name' => 'facebook',                                      'class' => 'copy2friendlyUrl',                    'desc' => $this->l("This is friend's facebook")                )            ),            'submit' => array(                'title' => $this->l('Save'),                'class' => 'button'            )        );        $this->fields_form['input'][] = array(            'type' => 'text',            'label' => $this->l('Position :'),            'size' => 3,            'name' => 'position'        );        $this->fields_form['input'][] = array(            'type' => 'radio',            'label' => $this->l('Status :'),            'name' => 'active',            'required' => false,            'class' => 't',            'is_bool' => true,            'values' => array(                array(                    'id' => 'active_on',                    'value' => 1,                    'label' => $this->l('Enabled')                ),                array(                    'id' => 'active_off',                    'value' => 0,                    'label' => $this->l('Disabled')                )            )        );        return parent::renderForm();    }}?>